import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


class Item extends Component {
    Del = (idx) => {
        console.log("delete!!");
        console.log("idx::" + idx);
    }

    render() {
        //물려받은 요소
        var curItem = this.props.item;
        var idx = this.props.index;

        return (
            <div id="DV_Thing">
                <button onClick={this.Del(idx)} > {curItem.text}</button>
            </div >
        )
    }
}

class List extends Component {

    static defaultProps = {
        itemArr: []
    }

    render() {
        //물려받은 부모의 배열

        //부모 컴퍼넌트로부터 받은 배열을 map
        var listItems = this.props.entries.map((one, idx) => { //item 은 배열 속 요소 하나
            <Item item={one} index={idx} ></Item >
        });

        //ToDoList라는 최상위 div 아래에 리턴
        return (
            <div id="ToDoList" >
                {listItems}
            </div>
        )
    }
}

export default List;
