import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
// import Child from './Child.js';

class App extends Component {

  // state와 하위 변수 생성
  constructor(props) {
    super(props);
    this.state = {
      content: "",
      itemArr: []
    }
  }


  Post = () => {
