    if (temp_string != "") {
      //배열에 string 추가해주기
      console.log("Arr::" + this.state.itemArr);
      tempArr = this.state.itemArr;
      tempArr.push(temp_string);

      this.setState({
        // itemArr: this.state.itemArr.push(temp_string)
        itemArr: tempArr
      });

      //비워주기
      this.setState({ content: "" });
      // this.Rearrange();
    }

    console.log("Arr::" + this.state.itemArr);
  }

