    <button id="BT_Thing" data-idx="${idx}" class="textField" onclick="Edit(${idx})">${param}</button>
    <button id="BT_Delete" data-idx="${idx}" onclick="Delete(${idx})">DELETE</button>
    </div>
    `
  }

  render() { //렌더 써주기 
    return (
      <div className="main" >
        <p>To Do List</p>
        <p></p><p></p><p></p><p></p>

        {/* 인풋필드 */}
        <input type="text" id="IF_Todo" value={this.state.content} onChange={this.inputUpdate} placeholder="리액트로 짠 To Do" />
        <button onClick={this.Post}> 추가요~ </button>

