import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
// import Child from './Child.js';

class App extends Component {

  constructor(props) {
    super(props); //컴퍼넌트의 생성자 함수를 미리 호풀한다.

    this.state = {
      content: "null"
    };

    console.log("constructor")
  }

  click = () => {
    alert(this.state.content);
  }

  //
  change = a => {
    console.log("change");
    this.setState({ content: a.target.value })
  }
  //
  static getDerivedStateFromProps() {
    console.log("getDerivedStateFromProps");
    return null;
  }
  componentDidMount() {
    console.log("componentDidMount");
  }

  shouldComponentUpdate() {
    console.log("shouldComponentUpdate");
    return true;
  }

  getSnapshotBeforeUpdate() {
    console.log("getSnapshotBeforeUpdate");
    return "";
  }

  componentDidUpdate() {
    console.log("componentDidUpdate");
  }

  render() { //렌더 써주기 
    console.log("render");
    return (
      <div className="main" >
        <p></p>
        <p></p>
        <p></p>

        <input type="text" value={this.state.content} onChange={this.change} />
        <button onClick={this.click}> alerfasdfsfast </button>

        {/* <Child todo={"clean"} />  자식에게 내려주기 */}

      </div>
    );
  }
}



export default App;



