import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Item from './Item.js';


class List extends Component {

    static defaultProps = {
        content: "",
        itemArr: []
    }

    Delete = () => {
        //목록에서 지워주면 알아서 그려진다(?)
    }

    render() {
        //리턴 형식, key가 필요함
        function generateLine(item) {
            return <div key={item.key} id="DV_Thing">
                <button key={item.key}>{item.text}</button>
                <button key={item.key + 1} onClick={this.Post}>삭제</button>
            </div >
        }

        //부모 컴퍼넌트로부터 받은 배열을 mapping
        var listItems = this.props.entries.map(generateLine);

        //ToDoList라는 최상위 div 아래에 리턴
        return (
            <div id="ToDoList">
                {listItems}
            </div>
        )
    }
}

export default List;
