}

class List extends Component {

    static defaultProps = {
        itemArr: []
    }



    render() {
        var tempArr = this.props.entries;

        //부모 컴퍼넌트로부터 받은 배열을 map
        var listItems = tempArr.map((_item) => { //item 은 배열 속 요소 하나
            return <Item item={_item} ></Item>
        });

        //ToDoList라는 최상위 div 아래에 리턴
        return (
            <div id="ToDoList">
                {listItems}
            </div>
        )
    }
}




