import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
// import Child from './Child.js';

class App extends Component {

  // state와 하위 변수 생성
  constructor(props) {
    super(props);
    this.state = {
      content: "",
      itemArr: []
    }
  }

  setLiteral = function (param, idx) {
    return `
    <div id="DV_Thing" data-idx="${idx}">
    <button id="BT_Thing" data-idx="${idx}" class="textField" onclick="Edit(${idx})">${param}</button>
    <button id="BT_Delete" data-idx="${idx}" onclick="Delete(${idx})">DELETE</button>
    </div>
    `
  }
  Post = () => {
    console.log("Post");
    if (document.getElementById("IF_Todo").value != "") {
      itemArr.push(document.getElementById("IF_Todo").value);
      document.getElementById("IF_Todo").value = "";
      Rearrange();
    }
  }

  Rearrange = function () {
    let literal = "";

    for (let i = 0; i < itemArr.length; i++) {
      literal += this.setLiteral(itemArr[i], i);
    }

    document.getElementById("DV_List").innerHTML = literal;
  }

  inputUpdate = temp => {
    this.setState({ content: temp.target.value })
  }

  render() { //렌더 써주기 
    console.log("render");
    return (
      <div className="main" >
        <p>To Do List</p>
        <p></p><p></p><p></p><p></p>

        {/* 인풋필드 */}
        <input type="text" value={this.state.content} onChange={this.inputUpdate} placeholder="리액트로 짠 To Do" />
        <button onClick={this.Post}> 추가요~ </button>

        <p></p><p></p><p></p><p></p>
        <div id="DV_List"> </div>

        {/* <Child todo={"clean"} />  자식에게 내려주기 */}




      </div>
    );
  }
}



export default App;

