import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Item from './Item.js';


class List extends Component {

    static defaultProps = {
        content: "",
        itemArr: []
    }

    // const newArr2 = arr.filter(num => num !== 3);



    // Delete = () => {
