import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
// import Child from './Child.js';

class App extends Component {


  // ㄴㅅㅁㅅㄷ생성자
  constructor(props) {
    super(props);
    this.state = { content: "" }
  }

  //함수 예시
  Post = () => {
    alert(this.state.content);
  }

  //함수
  inputUpdate = temp => {
    this.setState({ content: temp.target.value })
  }

  render() { //렌더 써주기 
    console.log("render");
    return (
      <div className="main" >
        <p>To Do List</p>
        <p></p><p></p><p></p><p></p>

        {/* 인풋필드 */}
        <input type="text" value={this.state.content} onChange={this.inputUpdate} placeholder="리액트로 짠 To Do" />
        <button onClick={this.Post}> 추가요~ </button>

        <p></p><p></p><p></p><p></p>
        {/* <Child todo={"clean"} />  자식에게 내려주기 */}



      </div>
    );
  }
}



export default App;
